#Function to open a popup window to display all running windows and give Name and Title
function Get-windows{
    function Add-Window
    {
	    Param (
		    $ID = "",
		    $Name = "",
            $MainWindowTitle = ""
	    )
	
	    $DataGridView_ID.Rows.Add($ID, $Name, $MainWindowTitle)
    }

    #Define Form
    $GetCurrentWindows                                          = New-Object System.Windows.Forms.Form
    $GetCurrentWindows.Text                                     = "Current Windows"
    $GetCurrentWindows.Name                                     = "Current Windows"
    $GetCurrentWindows.DataBindings.DefaultDataSourceUpdateMode = 0
    $GetCurrentWindows.Location                                 = New-Object System.Drawing.Point(10, 10)
    $GetCurrentWindows.ClientSize.Width                         = 2240
    $GetCurrentWindows.ClientSize.Height                        = 1120
    $GetCurrentWindows.AutoSize                                 = $true
    $GetCurrentWindows.MaximizeBox                              = $true
    $GetCurrentWindows.StartPosition                            = "CenterScreen"
    $GetCurrentWindows.icon                                     = [System.Drawing.Icon]::FromHandle((New-Object System.Drawing.Bitmap -Argument $stream).GetHIcon())

    #Defining DataGridView
    $DataGridView_ID                                            = New-Object System.Windows.Forms.DataGridView
    $DataGridView_ID.AllowUserToAddRows                         = $false
    $DataGridView_ID.AllowUserToDeleteRows                      = $false
    $DataGridView_ID.Top                                        = 0
    $DataGridView_ID.Left                                       = 0
    $DataGridView_ID.ColumnHeadersVisible                       = $true
    $DataGridView_ID.ColumnHeadersHeight                        = 20
    $DataGridView_ID.Enabled                                    = $true
    $DataGridView_ID.AutoSize                                   = $true

    #Add Columns
    $Column1                                                    = New-Object System.Windows.Forms.DataGridViewTextboxColumn
    $Column2                                                    = New-Object System.Windows.Forms.DataGridViewTextboxColumn
    $Column3                                                    = New-Object System.Windows.Forms.DataGridViewTextboxColumn

    $Column1.Name                                               = 'ID'
    $Column2.Name                                               = 'Name'
    $Column3.Name                                               = 'Main Window Title'

    $DataGridView_ID.Columns.Add($Column1)
    $DataGridView_ID.Columns.Add($Column2)
    $DataGridView_ID.Columns.Add($Column3)

    $GetCurrentWindows.Controls.AddRange(($DataGridView_ID))

    $CurrentWindows = Get-Process | Where-Object { $_.MainWindowTitle } | Select-Object -Property ID,Name,MainWindowTitle

    Foreach ($Window in $CurrentWindows){

        $WindowID = $Window | Select-Object -ExpandProperty ID
        $WindowName = $Window | Select-Object -ExpandProperty Name
        $WindowTitle = $Window | Select-Object -ExpandProperty MainWindowTitle
        Add-Window "$WindowID" "$WindowName" "$WindowTitle"
    }

    $DataGridView_ID.Columns | Foreach-Object{
        $_.AutoSizeMode = [System.Windows.Forms.DataGridViewAutoSizeColumnMode]::AllCells
    }
    $GetCurrentWindows.ShowDialog()
}

Function Get-Window{
    Get-windows    

}

#starts a loop waiting for window to appear and writes to log file


#Launches the Application entered into the Window with any Arguments entered
Function Lunch-Application {

    $Application = $TextBox_Application.text
    $Application = "`"$Application`""

     if($TextBox_Arguments.text.Length -gt 0){

        start-process -filepath $Application -ArgumentList $TextBox_Arguments.text

     }
     else{

        start-process -filepath $Application
        

     }

}


#Kills the selected application by Window Name
Function Kill-Application {

    $SelectedWindow = $TextBox_WindowName.text
    if ($Null -ne $SelectedWindow){
    get-process | where-object {$_.MainWindowTitle -eq $SelectedWindow} | stop-process
    }
    write-host "Killing App"
}

Function Benchmark-Time {
    
    #starts a loop waiting for window to appear and writes to log file
    Function Start-TimerLoop {

        #Function that returns true if the selected window is not seen running in the Process list 
            Function Get-ReferenceWindow {

                $SelectedWindow = $TextBox_WindowName.text
                $CurrentWindows = Get-Process | Where-Object { $_.MainWindowTitle } | Select-Object -Property MainWindowTitle

                if ($CurrentWindows.MainWindowTitle -notcontains $SelectedWindow){

                    return $true

                }
                else{
                    return $false
                }

            }
        

        $StopWatch = New-Object System.Diagnostics.Stopwatch

            if($TextBox_Timeout.text.Length -ge 0 -or $TextBox_Timeout.text -gt 0){
                $TimeoutTimer = New-Object System.Diagnostics.Stopwatch
                $Time = $TextBox_Timeout.text
                $TimeoutTimer.start()
                $startTimout = $True
            }
            else{
                $startTimout = $false   
            }

            While (Get-ReferenceWindow){

            $StopWatch.Start()

            
                if($TimeoutTimer.Elapsed.TotalSeconds -ge $Time -and $startTimout -eq $True){
                    write-host "Timed out, Breaking"

                    $TimeoutTimer.Stop()
                    $StopWatch.Stop()

                    $OUTPUT = (new-object -ComObject wscript.shell).Popup("Application Timed Out", 0, "Error", 0x0)

                return $false
                
            }
        }
        $StopwatchElapsed = $StopWatch.Elapsed
        write-host "Application Start time: $StopwatchElapsed"
        $StopWatch.Stop()

        $StopWatchResult = $StopWatch.Elapsed | Select-Object -ExpandProperty TotalSeconds
        Add-Content "$LogFile" "$StopWatchResult"
        $script:TimerAverage += $StopWatchResult

        $StopWatch.reset()

        return $true

    }


    $SelectedWindow = $TextBox_WindowName.text
    $LogFile = $TextBox_Results.text
    $script:CompletedLaunch = $false
        if ($null -eq $LogFile){
            $LogFile = "C:\Temp\ApplicationTimer.txt"
        }
    $Iterations = $TextBox_Iterations.text
        if ($Null -eq $Iterations){
            $Iterations = 1
        }
    $Delay = $TextBox_Delay.text
        if ($Null -eq $Delay){
            $Delay = 0
        }

    Add-Content "$LogFile" "$SelectedWindow"

    $i = 1

    while ($i -le $Iterations){
        
        [int]$pct1 = ($i/$Iterations)*100
        #update the progress bar
        $ProgressBar1.Value = $pct1  

        $launch = Lunch-Application
        #Lunch-Application
        Start-Job -ScriptBlock{ $launch } | wait-job | Receive-Job
        
            if(Start-TimerLoop -eq $false){
                write-host "return false and passing to break"
                break
            }
            else{

            Kill-Application


            $i++
            start-sleep -Milliseconds $Delay
            }
    }
    
    $script:TimerAverage = $script:TimerAverage | Measure-Object -Average
    Add-Content "$LogFile" "$script:TimerAverage"
    write-host "Final Break"
    $ProgressBar1.Value = 0
    Kill-Application
}

